mapper.select:
SELECT M.*, R.ROUTES FROM mountain M INNER JOIN (SELECT MNTNID, COUNT(*) AS ROUTES FROM mrmapping GROUP BY MNTNID) AS R ON M.MNTNID = R.MNTNID WHERE M.MNTNNAME LIKE CONCAT('%',#{keyword},'%')
AND M.MNTNADDR LIKE CONCAT('%',#{mntnaddr},'%')
AND M.MNTNHIGH BETWEEN #{mntnhigh1} AND #{mntnhigh2}
AND R.ROUTES BETWEEN #{route1} AND #{route2}
